INSERT INTO met_img VALUES('46','スマートクルーズ制御システムSCC','','','私達のAMP+ケーブル、コネクタ、線束と端子シリーズは電池と車両の内部と周辺で高圧と低圧電力を安全かつ確実に輸送できて、あなたに走行働力を提供します。','<p><span style=\"font-size: 16px;\"><span style=\"color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;, sans-serif; background-color: rgb(255, 255, 255);\">前方車両をレーダーで感知した後、自動的に減速や加速制御を行い、前方車両と一定の距離を保つシステムのこと。</span>自動車産業、情報産業、交通分野、車の部品サービスを含めて、実際には1つの自働車社会、知能的な自働車社会です。</span></p>','231','233','0','1','0','0','../upload/202002/1582281693.jpg','','智能巡航控制系统SCC*../upload/202002/1582281524.jpg*900x383','0','5','2020-05-15 16:29:58','2020-05-15 16:29:58','admin','0','0','','ja','','','','','','','','','','0','1','','','900x383','0','','','');
